import React, { useState, useEffect } from 'react';
import { FileText, Home, Skull, Bug, Zap, Terminal } from 'lucide-react';
import ThreatChart from './ThreatChart';
import RiskGauge from './RiskGauge';
import SystemStats from './SystemStats';
import OverallRiskGauge from './OverallRiskGauge';
import RiskBreakdownChart from './RiskBreakdownChart';
import SecurityHeaders from './SecurityHeaders';
import DetectedTechnologies from './DetectedTechnologies';
import InformationSection from './InformationSection';
import VulnerabilityTrend from './VulnerabilityTrend';
import AdditionalSecurityMetrics from './AdditionalSecurityMetrics';
import SystemScannerBox from './SystemScannerBox';
import SystemScanner from './SystemScanner';
import axios from 'axios';

const Dashboard = ({ onBackToLanding }) => {
  const [currentView, setCurrentView] = useState('dashboard');
  const [systemStats, setSystemStats] = useState(null);
  const [threatTrends, setThreatTrends] = useState([]);
  const [threatDistribution, setThreatDistribution] = useState({});
  const [scanResults, setScanResults] = useState(null);
  const [isScanning, setIsScanning] = useState(false);
  const [newUrl, setNewUrl] = useState('');

  useEffect(() => {
    fetchSystemStats();
    fetchThreatTrends();
    fetchThreatDistribution();
  }, []);

  const fetchSystemStats = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/system-stats');
      setSystemStats(response.data.stats);
    } catch (error) {
      console.error('Error fetching system stats:', error);
      setSystemStats({
        cpu_usage: 45,
        memory_usage: 67,
        disk_usage: 32,
        network_activity: 'Normal',
        active_threats: 3,
        last_scan: '2 hours ago',
        firewall_status: 'Active',
        real_time_protection: 'Enabled'
      });
    }
  };

  const fetchThreatTrends = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/threat-trends');
      setThreatTrends(response.data.trends);
    } catch (error) {
      console.error('Error fetching threat trends:', error);
    }
  };

  const fetchThreatDistribution = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/threat-distribution');
      setThreatDistribution(response.data.distribution);
    } catch (error) {
      console.error('Error fetching threat distribution:', error);
    }
  };

  const handleNavigateToScanner = () => setCurrentView('scanner');
  const handleBackToDashboard = () => setCurrentView('dashboard');

  if (currentView === 'scanner') {
    return <SystemScanner onBackToDashboard={handleBackToDashboard} />;
  }

  return (
    <div className="min-h-screen bg-dark-bg text-white p-6">
      {/* Malware-styled Header */}
      <div className="mb-8 relative">
        <div className="absolute inset-0 bg-gradient-to-r from-red-primary/10 to-transparent rounded-lg"></div>
        <div className="relative bg-dark-card border border-red-primary/30 rounded-xl p-6">
          {/* Header content here */}
        </div>
      </div>

      {/* System Stats Cards */}
      {systemStats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-dark-card p-6 rounded-xl border border-red-primary/30 relative overflow-hidden transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            {/* CPU */}
          </div>
          <div className="bg-dark-card p-6 rounded-xl border border-red-primary/30 relative overflow-hidden animate-glow transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            {/* Memory */}
          </div>
          <div className="bg-dark-card p-6 rounded-xl border border-red-accent/30 relative overflow-hidden transition-all duration-300 hover:border-red-accent/50 hover:scale-105 cursor-pointer">
            {/* Disk */}
          </div>
          <div className="bg-dark-card p-6 rounded-xl border border-red-secondary/30 relative overflow-hidden transition-all duration-300 hover:border-red-secondary/50 hover:scale-105 cursor-pointer">
            {/* Threats */}
          </div>
        </div>
      )}

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column */}
        <div className="lg:col-span-2 space-y-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
            <div className="transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
              <OverallRiskGauge />
            </div>
            <div className="transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
              <RiskBreakdownChart />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
            <div className="transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
              <SecurityHeaders />
            </div>
            <div className="transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
              <DetectedTechnologies />
            </div>
          </div>

          <div className="transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            <InformationSection />
          </div>

          <div className="bg-dark-card p-6 rounded-xl border border-red-primary/30 relative overflow-hidden transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            <ThreatChart data={threatTrends} />
          </div>

          <div className="transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            <SystemScannerBox onNavigateToScanner={handleNavigateToScanner} />
          </div>

          <div className="mt-8 transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            <AdditionalSecurityMetrics />
          </div>
        </div>

        {/* Right Column */}
        <div className="space-y-8">
          <div className="transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            <VulnerabilityTrend />
          </div>
          <div className="transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            <RiskGauge />
          </div>
          <div className="transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            <SystemStats stats={systemStats} onNavigateToScanner={handleNavigateToScanner} />
          </div>
          <div className="bg-dark-card p-6 rounded-xl border border-gray-700 transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            {/* Threat Types */}
          </div>

          {/* Creators Card */}
          <div className="bg-dark-card p-6 rounded-xl border border-red-primary/30 relative overflow-hidden transition-all duration-300 hover:border-red-primary/50 hover:scale-105 cursor-pointer">
            <h3 className="text-lg font-semibold mb-4 text-red-primary">Projects Creators</h3>
            <ul className="space-y-2 text-gray-300">
              <li>Person 1</li>
              <li>Person 2</li>
              <li>Person 3</li>
            </ul>
            <div className="mt-4 text-sm text-gray-400 flex items-center space-x-1">
              <span>Made with</span>
              <span className="animate-pulse text-red-500">❤️</span>
              <span>by community</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
