import React, { useState, useEffect } from 'react';
import { Shield, AlertTriangle, FileText, Activity, Upload, Scan, Home, Skull, Bug, Zap, Terminal } from 'lucide-react';
import ThreatChart from './ThreatChart';
import RiskGauge from './RiskGauge';
import UrlScanner from './UrlScanner';
import SystemStats from './SystemStats';
import axios from 'axios';

const Dashboard = () => {
  const [systemStats, setSystemStats] = useState(null);
  const [threatTrends, setThreatTrends] = useState([]);
  const [threatDistribution, setThreatDistribution] = useState({});
  const [scanResults, setScanResults] = useState(null);
  const [isScanning, setIsScanning] = useState(false);

  useEffect(() => {
    fetchSystemStats();
    fetchThreatTrends();
    fetchThreatDistribution();
  }, []);

  const fetchSystemStats = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/system-stats');
      setSystemStats(response.data.stats);
    } catch (error) {
      console.error('Error fetching system stats:', error);
    }
  };

  const fetchThreatTrends = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/threat-trends');
      setThreatTrends(response.data.trends);
    } catch (error) {
      console.error('Error fetching threat trends:', error);
    }
  };

  const fetchThreatDistribution = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/threat-distribution');
      setThreatDistribution(response.data.distribution);
    } catch (error) {
      console.error('Error fetching threat distribution:', error);
    }
  };

  const handleQuickScan = async () => {
    setIsScanning(true);
    try {
      const response = await axios.post('http://localhost:5000/api/quick-scan');
      setScanResults(response.data.scan_results);
    } catch (error) {
      console.error('Error performing quick scan:', error);
    } finally {
      setIsScanning(false);
    }
  };

  const getRiskColor = (level) => {
    switch (level?.toLowerCase()) {
      case 'critical': return 'text-red-500';
      case 'high': return 'text-orange-500';
      case 'medium': return 'text-yellow-500';
      case 'low': return 'text-blue-500';
      default: return 'text-green-500';
    }
  };

  return (
    <div className="min-h-screen bg-dark-bg text-white p-6">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Shield className="w-8 h-8 text-cyber-blue" />
            <h1 className="text-3xl font-bold text-white">Cyber Threat Analyzer</h1>
          </div>
          <div className="flex items-center space-x-4">
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
              <span className="text-sm text-gray-300">System Protected</span>
            </div>
            <button
              onClick={handleQuickScan}
              disabled={isScanning}
              className="flex items-center space-x-2 bg-cyber-blue hover:bg-blue-600 px-4 py-2 rounded-lg transition-colors disabled:opacity-50"
            >
              <Scan className="w-4 h-4" />
              <span>{isScanning ? 'Scanning...' : 'Quick Scan'}</span>
            </button>
          </div>
        </div>
      </div>

      {/* System Stats Cards */}
      {systemStats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-400 text-sm">Total Scans</p>
                <p className="text-2xl font-bold text-white">{systemStats.total_scans?.toLocaleString()}</p>
              </div>
              <FileText className="w-8 h-8 text-cyber-blue" />
            </div>
          </div>

          <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-400 text-sm">Threats Blocked</p>
                <p className="text-2xl font-bold text-cyber-red">{systemStats.threats_blocked}</p>
              </div>
              <AlertTriangle className="w-8 h-8 text-cyber-red" />
            </div>
          </div>

          <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-400 text-sm">Files Quarantined</p>
                <p className="text-2xl font-bold text-yellow-500">{systemStats.files_quarantined}</p>
              </div>
              <Shield className="w-8 h-8 text-yellow-500" />
            </div>
          </div>

          <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-400 text-sm">System Health</p>
                <p className={`text-2xl font-bold ${getRiskColor(systemStats.system_health)}`}>
                  {systemStats.system_health}
                </p>
              </div>
              <Activity className="w-8 h-8 text-cyber-green" />
            </div>
          </div>
        </div>
      )}

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column */}
        <div className="lg:col-span-2 space-y-8">
          {/* URL Scanner */}
          <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
            <h2 className="text-xl font-semibold mb-4 flex items-center">
              <Upload className="w-5 h-5 mr-2 text-cyber-blue" />
              URL Threat Scanner
            </h2>
            <UrlScanner />
          </div>

          {/* Threat Trends Chart */}
          <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
            <h2 className="text-xl font-semibold mb-4">Threat Trends (7 Days)</h2>
            <ThreatChart data={threatTrends} />
          </div>

          {/* Quick Scan Results */}
          {scanResults && (
            <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
              <h2 className="text-xl font-semibold mb-4">Latest Scan Results</h2>
              <div className="space-y-4">
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  <div className="text-center">
                    <p className="text-2xl font-bold text-cyber-blue">{scanResults.files_scanned}</p>
                    <p className="text-sm text-gray-400">Files Scanned</p>
                  </div>
                  <div className="text-center">
                    <p className="text-2xl font-bold text-cyber-red">{scanResults.threats_found}</p>
                    <p className="text-sm text-gray-400">Threats Found</p>
                  </div>
                  <div className="text-center">
                    <p className="text-2xl font-bold text-cyber-green">{scanResults.scan_duration}s</p>
                    <p className="text-sm text-gray-400">Scan Time</p>
                  </div>
                  <div className="text-center">
                    <p className="text-2xl font-bold text-yellow-500">{scanResults.status}</p>
                    <p className="text-sm text-gray-400">Status</p>
                  </div>
                </div>

                {scanResults.findings.length > 0 && (
                  <div className="mt-4">
                    <h3 className="font-semibold mb-2">Findings:</h3>
                    <div className="space-y-2">
                      {scanResults.findings.map((finding, index) => (
                        <div key={index} className="bg-gray-800 p-3 rounded-lg">
                          <div className="flex justify-between items-start">
                            <div>
                              <p className="font-medium">{finding.file_path}</p>
                              <p className="text-sm text-gray-400">{finding.threat_type}</p>
                            </div>
                            <div className="text-right">
                              <span className={`px-2 py-1 rounded text-xs ${
                                finding.severity === 'High' ? 'bg-red-600' :
                                finding.severity === 'Medium' ? 'bg-yellow-600' : 'bg-blue-600'
                              }`}>
                                {finding.severity}
                              </span>
                              <p className="text-xs text-gray-400 mt-1">{finding.action_taken}</p>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>

        {/* Right Column */}
        <div className="space-y-8">
          {/* Risk Gauge */}
          <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
            <h2 className="text-xl font-semibold mb-4">System Risk Level</h2>
            <RiskGauge value={systemStats?.threats_blocked || 0} />
          </div>

          {/* System Stats */}
          <SystemStats stats={systemStats} />

          {/* Threat Distribution */}
          <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
            <h2 className="text-xl font-semibold mb-4">Threat Types</h2>
            <div className="space-y-3">
              {Object.entries(threatDistribution).map(([type, count]) => (
                <div key={type} className="flex justify-between items-center">
                  <span className="text-sm">{type}</span>
                  <div className="flex items-center space-x-2">
                    <div className="w-20 bg-gray-700 rounded-full h-2">
                      <div 
                        className="bg-cyber-blue h-2 rounded-full" 
                        style={{ width: `${(count / Math.max(...Object.values(threatDistribution))) * 100}%` }}
                      ></div>
                    </div>
                    <span className="text-sm text-gray-400 w-8">{count}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
