import React from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';

ChartJS.register(ArcElement, Tooltip, Legend);

const OverallRiskGauge = ({ riskScore = 0 }) => {
  const data = {
    datasets: [
      {
        data: [riskScore, 100 - riskScore],
        backgroundColor: ['#ff4444', '#2a1a1a'],
        borderWidth: 0,
        cutout: '75%',
      },
    ],
  };
  const options = { responsive: true, plugins: { legend: { display: false }, tooltip: { enabled: false } } };

  return (
    <div className="bg-dark-card p-6 rounded-xl border border-red-primary/30">
      <h3 className="text-lg font-semibold mb-4">Overall Risk</h3>
      <div className="relative h-48">
        <Doughnut data={data} options={options} />
        <div className="absolute inset-0 flex items-center justify-center text-red-primary text-4xl font-bold">
          {riskScore}%
        </div>
      </div>
    </div>
  );
};

export default OverallRiskGauge;
