import React, { useState, useEffect } from 'react';
import { FileText, Home, Skull, Bug, Zap, Terminal } from 'lucide-react';
import axios from 'axios';

import ThreatChart from './ThreatChart';
import OverallRiskGauge from './OverallRiskGauge';
import RiskBreakdownChart from './RiskBreakdownChart';
import RiskGauge from './RiskGauge';
import SystemStats from './SystemStats';
import SecurityHeaders from './SecurityHeaders';
import DetectedTechnologies from './DetectedTechnologies';
import InformationSection from './InformationSection';
import VulnerabilityTrend from './VulnerabilityTrend';
import AdditionalSecurityMetrics from './AdditionalSecurityMetrics';
import SystemScannerBox from './SystemScannerBox';
import SystemScanner from './SystemScanner';

const Dashboard = ({ onBackToLanding }) => {
  const [currentView, setCurrentView] = useState('dashboard');
  const [systemStats, setSystemStats] = useState(null);
  const [threatTrends, setThreatTrends] = useState([]);
  const [threatDistribution, setThreatDistribution] = useState({});
  const [scanResults, setScanResults] = useState(null);
  const [riskBreakdown, setRiskBreakdown] = useState(null);
  const [overallRisk, setOverallRisk] = useState(null);
  const [isScanning, setIsScanning] = useState(false);
  const [newUrl, setNewUrl] = useState('');

  useEffect(() => {
    fetchSystemStats();
    fetchThreatTrends();
    fetchThreatDistribution();
    fetchRiskBreakdown();
  }, []);

  // Fetch system stats
  const fetchSystemStats = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/system-stats');
      setSystemStats(data.stats);
    } catch (err) {
      console.error('System stats fetch error:', err);
      setSystemStats({
        cpu_usage: 45,
        memory_usage: 67,
        disk_usage: 32,
        network_activity: 'Normal',
        active_threats: 3,
        last_scan: '2 hours ago',
        firewall_status: 'Active',
        real_time_protection: 'Enabled',
        total_scans: 12345,
        threats_blocked: 78,
        files_quarantined: 12,
        system_health: 'Good'
      });
    }
  };

  // Threat trends
  const fetchThreatTrends = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/threat-trends');
      setThreatTrends(data.trends);
    } catch (err) {
      console.error('Threat trends fetch error:', err);
      setThreatTrends([
        { date: '2024-08-12', threats_detected: 12, files_scanned: 1500, blocked_attempts: 8 },
        { date: '2024-08-13', threats_detected: 8, files_scanned: 1200, blocked_attempts: 5 },
        { date: '2024-08-14', threats_detected: 15, files_scanned: 1800, blocked_attempts: 12 },
        { date: '2024-08-15', threats_detected: 6, files_scanned: 900, blocked_attempts: 3 },
        { date: '2024-08-16', threats_detected: 20, files_scanned: 2100, blocked_attempts: 18 },
        { date: '2024-08-17', threats_detected: 11, files_scanned: 1600, blocked_attempts: 9 },
        { date: '2024-08-18', threats_detected: 14, files_scanned: 1750, blocked_attempts: 11 }
      ]);
    }
  };

  // Threat distribution
  const fetchThreatDistribution = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/threat-distribution');
      setThreatDistribution(data.distribution);
    } catch (err) {
      console.error('Threat distribution fetch error:', err);
      setThreatDistribution({
        'Malware': 45,
        'Viruses': 32,
        'Trojans': 28,
        'Spyware': 19,
        'Adware': 15,
        'Rootkits': 12
      });
    }
  };

  // Risk breakdown
  const fetchRiskBreakdown = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/risk-breakdown');
      setRiskBreakdown(data.breakdown); // Example: { ssl:8, headers:6, malware:3, open_ports:7, csp:4 }
      setOverallRisk(data.overallRisk); // Example: 65
    } catch (err) {
      console.error('Risk breakdown fetch error:', err);
      setRiskBreakdown({ ssl: 8, headers: 6, malware: 3, open_ports: 7, csp: 4 });
      setOverallRisk(45);
    }
  };

  // Scan URL
  const handleScanUrl = async () => {
    if (!newUrl.trim()) return;
    setIsScanning(true);
    try {
      const { data } = await axios.post('http://localhost:5000/api/scan-url', { url: newUrl });
      if (data.success) {
        setScanResults(data.analysis);
        // Update charts dynamically based on scan
        setRiskBreakdown(data.analysis.riskBreakdown);
        setOverallRisk(data.analysis.overallRisk);
      }
    } catch (err) {
      console.error('Scan failed:', err);
    } finally {
      setIsScanning(false);
    }
  };

  if (currentView === 'scanner') return <SystemScanner onBackToDashboard={() => setCurrentView('dashboard')} />;

  return (
    <div className="min-h-screen bg-dark-bg text-white p-6">
      {/* Header + URL input */}
      <div className="mb-8">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-3xl font-bold text-red-primary">THREAT DETECTION ACTIVE</h1>
          <button onClick={onBackToLanding} className="bg-red-primary px-4 py-2 rounded-lg">Back</button>
        </div>
        <div className="flex space-x-2">
          <input
            value={newUrl}
            onChange={(e) => setNewUrl(e.target.value)}
            placeholder="Enter target URL..."
            className="bg-dark-secondary px-4 py-2 rounded-lg flex-1"
          />
          <button onClick={handleScanUrl} className="bg-red-primary px-4 py-2 rounded-lg">{isScanning ? 'Scanning...' : 'Analyze'}</button>
        </div>
      </div>

      {/* Charts Row */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <OverallRiskGauge riskScore={overallRisk} />
        <RiskBreakdownChart breakdown={riskBreakdown} />
        <RiskGauge value={systemStats?.threats_blocked || 0} />
      </div>

      {/* Rest of dashboard content */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 space-y-8">
          <ThreatChart data={threatTrends} />
          <SystemScannerBox onNavigateToScanner={() => setCurrentView('scanner')} />
          <InformationSection />
          <SecurityHeaders />
          <DetectedTechnologies />
          <AdditionalSecurityMetrics />
        </div>
        <div className="space-y-8">
          <SystemStats stats={systemStats} />
          <VulnerabilityTrend />
          {/* Threat Distribution */}
          <div className="bg-dark-card p-6 rounded-xl border border-gray-700">
            <h2 className="text-xl mb-2">Threat Types</h2>
            {Object.entries(threatDistribution).map(([type, count]) => (
              <div key={type} className="flex justify-between items-center mb-1">
                <span>{type}</span>
                <div className="flex items-center space-x-2">
                  <div className="w-20 bg-gray-700 rounded-full h-2">
                    <div
                      className="bg-cyber-blue h-2 rounded-full"
                      style={{ width: `${(count / Math.max(...Object.values(threatDistribution))) * 100}%` }}
                    ></div>
                  </div>
                  <span className="text-gray-400">{count}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
