import React from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { Line } from 'react-chartjs-2';
import { CheckCircle } from 'lucide-react';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const VulnerabilityTrend = () => {
  // Generate random vulnerability count
  const vulnCount = Math.floor(Math.random() * 15) + 3;

  // Generate trend data
  const trendData = Array.from({ length: 7 }, () => Math.floor(Math.random() * 20) + 5);

  const data = {
    labels: ['', '', '', '', '', '', ''],
    datasets: [
      {
        data: trendData,
        borderColor: '#ff4444',
        backgroundColor: 'rgba(255, 68, 68, 0.1)',
        borderWidth: 2,
        fill: true,
        tension: 0.4,
        pointRadius: 3,
        pointBackgroundColor: '#ff4444',
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        enabled: false,
      },
    },
    scales: {
      x: {
        display: false,
      },
      y: {
        display: false,
      },
    },
    elements: {
      point: {
        hoverRadius: 0,
      },
    },
  };

  return (
    <div className="bg-dark-card p-6 rounded-xl border border-red-primary/30 relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-br from-red-primary/5 to-transparent"></div>
      <div className="relative">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-2">
            <CheckCircle className="w-5 h-5 text-red-primary" />
            <span className="text-2xl font-bold text-white">{vulnCount}</span>
          </div>
          <div className="text-right">
            <div className="w-16 h-8">
              <Line data={data} options={options} />
            </div>
          </div>
        </div>
        <p className="text-sm text-gray-400">Vulnerabilities Found</p>
      </div>
    </div>
  );
};

export default VulnerabilityTrend;
